public class Dowhileloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
  int i=0;
  do
  {
	  System.out.println("Value of i:" +i);
	  i=i+2;
  }
  while(i<10);
  {
  System.out.println("while loop exist.Value of i:" +i);
  }
  
  
	}

}
